import java.util.Scanner;

public class Addition 
{
	// A variable is a location in the computers memory that is stores and can be used later.
	// Format of a variable is "type name"
	
	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		// Creates area in memory specified for a "Scanner" object
		// "new" actually creates the object
		// System.in allows the Scanner to read information types by the user
		Scanner input = new Scanner(System.in);

		// variable1 + variable2 = result
		// Print the result
		int number1;
		int number2;
		
		// number1 = 200;
		// number2 = 400;
		System.out.print("Enter first number: ");
		number1 = input.nextInt();
		
		System.out.print("Enter second number: ");
		number2 = input.nextInt();
		
		System.out.printf("Result: %d", number1 + number2);
	}

}
